//! Macro expansion for XR-DSL\n\nuse crate::ast::*;\n\n// TODO: Implement macro system\n// For now, just pass through\npub fn expand_macros(expr: Expr) -> anyhow::Result<Expr> {\n    Ok(expr)\n}\n\npub fn expand_top(top: Top) -> anyhow::Result<Top> {\n    match top {\n        Top::Behavior(mut b) => {\n            b.update.body = expand_macros(b.update.body)?;\n            if let Some(ref mut sel) = b.on_select {\n                sel.body = expand_macros(sel.body)?;\n            }\n            Ok(Top::Behavior(b))\n        }\n    }\n}"